mapper.select:
SELECT COUNT(R_NO)
FROM REVIEW
WHERE M_NO = #{ m_no }

mapper.update:
UPDATE REVIEW
	SET R_GOOD = R_GOOD + 1
	WHERE R_NO = #{ r_no }

UPDATE REVIEW
	SET R_GOOD = R_GOOD - 1
	WHERE R_NO = #{ r_no }

UPDATE REVIEW
	SET R_BAD = R_BAD + 1
	WHERE R_NO = #{ r_no }

UPDATE REVIEW
	SET R_BAD = R_BAD - 1
	WHERE R_NO = #{ r_no }

UPDATE PLAY
	SET P_GOOD = P_GOOD + 1
	WHERE P_NO = #{ p_no }

UPDATE PLAY
	SET P_BAD = P_BAD + 1
	WHERE P_NO = #{ p_no }
